mapper.insert:
INSERT INTO PRODUCT_IMAGE
(	
	 PRODUCT_NO
	,FILE_NO
	,FILE_PATH
	,FILE_NAME
	,REG_NO
	,REG_DATE
)VALUES(
	  #{productNo}
	, PRODUCTIMAGE_SEQ.NEXTVAL 
	, #{filePath} 
	, #{fileName} 
	, #{regNo} 
	, SYSDATE
)

mapper.select:
SELECT COUNT(*)
  FROM LIKEPRODUCT
 WHERE PRODUCT_NO = #{productNo}

SELECT B.NICKNAME AS NICKNAME , B.REG_NO AS REGNO , B.CONTENT AS CONTENT , B.MOD_NO AS MODNO , B.MOD_DATE AS MODDATE , B.REG_DATE AS REGDATE , B.PRODUCT_NO AS PRODUCTNO , B.TITLE AS TITLE , B.ADDRESS AS ADDRESS , B.PRICE AS PRICE , B.HIT AS HIT , B.IMAGES AS IMAGES , B.LIKECOUNT AS LIKE_COUNT FROM ( SELECT ROWNUM AS RN , A.* FROM (SELECT M.NICKNAME , P.REG_NO , P.CONTENT , P.MOD_NO , P.MOD_DATE , P.REG_DATE , P.PRODUCT_NO , P.TITLE , P.ADDRESS , P.PRICE , P.HIT , NVL(I.IMAGES , '') AS IMAGES , NVL(L.LIKECOUNT,0) AS LIKECOUNT FROM MEMBER M INNER JOIN PRODUCT P ON M.MEMBER_NO = P.REG_NO LEFT OUTER JOIN (SELECT PRODUCT_NO AS PNO , LISTAGG(FILE_PATH , ',') WITHIN GROUP (ORDER BY PRODUCT_NO)IMAGES FROM PRODUCT_IMAGE GROUP BY PRODUCT_NO) I ON P.PRODUCT_NO = I.PNO LEFT OUTER JOIN (SELECT PRODUCT_NO , COUNT(REG_NO) AS LIKECOUNT FROM LIKEPRODUCT GROUP BY PRODUCT_NO) L ON P.PRODUCT_NO = L.PRODUCT_NO
WHERE P.STATUS = 1 AND P.DEL_YN = 'N' AND M.STATUS = 0
WHERE P.STATUS = 1 AND P.DEL_YN = 'N' AND M.STATUS = 0 AND P.ADDRESS LIKE '%' || #{city} || '%'
WHERE P.STATUS = 1 AND P.DEL_YN = 'N' AND M.STATUS = 0 AND P.ADDRESS LIKE '%' || #{region} || '%'
) A ) B WHERE B.RN BETWEEN #{beginRecord} AND #{endRecord}
