mapper.select:
SELECT B. REPLY_NO, B.REG_NO, B.REPLY_CONTENT, B.MOD_NO, B.DEL_YN
  FROM (SELECT ROWNUM AS RN, A. REPLY_NO, A. REG_NO, A.REPLY_CONTENT, A.MOD_NO, A.DEL_YN
          FROM (SELECT REPLY_NO, REG_NO, REPLY_CONTENT,MOD_NO, DEL_YN 
                  FROM REPLY WHERE BOARD_NO = #{boardNo}
                 ORDER BY REPLY_NO DESC) A) B
 WHERE B.RN BETWEEN #{beginRecord} AND #{endRecord}

mapper.update:
UPDATE REPLY 
SET  DEL_YN = 'Y' 
WHERE  REPLY_NO = #{replyNo}
